import groovy.io.FileType

import myphotos.Topic
import myphotos.Photo

class BootStrap {

    def init = { servletContext ->

        // Photos are in Base64 Format
        def photos = [
                "",
                "",
                ""
        ]

        def topic = new Topic(name: "Test")
        for (int j = 0; j < photos.size(); j++){
            def dat = new Photo(data: photos[j].decodeBase64())
            topic.addToPhotos(dat)
        }
        topic.save(flush: true)
    }
    def destroy = {
    }
}
